INSERT INTO `newpost` (`id_post`, `name_post`, `image_post`, `summery_post`, `content_post`, `id_category`, `status_display_post`, `date_post`, `productLineCode`) VALUES
(93, 'Giới thiệu về cây sen đá', 'sen.jpg', '<p>C&acirc;y sen đ&aacute;&nbsp;hay c&ograve;n gọi l&agrave;&nbsp;Li&ecirc;n đ&agrave;i,&nbsp;hoa đ&aacute;&nbsp;(T&ecirc;n tiếng Anh l&agrave;&nbsp;Succulent)&nbsp;l&agrave; lo&agrave;i rất dễ sống, ph&aacute;t triển chậm v&agrave; sống l&acirc;u, kh&ocirc;ng đ&ograve;i hỏi được chăm s&oacute;c thường xuy&ecirc;n.</p>\r\n', '<p><strong>C&acirc;y sen đ&aacute;</strong>&nbsp;hay c&ograve;n gọi l&agrave;&nbsp;<strong>Li&ecirc;n đ&agrave;i</strong>,<strong>&nbsp;hoa đ&aacute;&nbsp;</strong>(T&ecirc;n tiếng Anh l&agrave;<strong>&nbsp;Succulent</strong>)&nbsp;l&agrave; lo&agrave;i rất dễ sống, ph&aacute;t triển chậm v&agrave; sống l&acirc;u, kh&ocirc;ng đ&ograve;i hỏi được chăm s&oacute;c thường xuy&ecirc;n.&nbsp;Sen đ&aacute; l&agrave; giống c&acirc;y&nbsp;nhỏ, gần như kh&ocirc;ng c&oacute; th&acirc;n m&agrave; chỉ thấy l&aacute;, l&agrave; giống c&acirc;y mọng nước v&agrave; đặc biệt l&aacute; thường xếp th&agrave;nh h&igrave;nh như những b&ocirc;ng hoa, nhất l&agrave; hoa sen. Lo&agrave;i c&acirc;y n&agrave;y ưa&nbsp;mọc tr&ecirc;n đ&aacute;, sỏi, nhưng nơi kh&ocirc; cằn n&ecirc;n mới được gọi l&agrave;&nbsp;<strong>hoa sen đ&aacute;</strong>.&nbsp; B&ecirc;n cạnh đ&oacute;, hoa sen đ&aacute; rất dễ trồng, n&oacute; c&oacute; thể th&iacute;ch nghi với mọi loại kh&iacute; hậu, mọi địa h&igrave;nh v&agrave; sống quanh năm, khi l&aacute; rụng c&oacute; thể nảy chồi từ đ&oacute; v&agrave; mọc l&ecirc;n c&acirc;y mới. Ch&iacute;nh v&igrave; thế&nbsp;<strong>c&acirc;y sen đ&aacute;</strong>&nbsp;mang &yacute; nghĩa về một&nbsp;<strong>t&igrave;nh y&ecirc;u bền chặt</strong>, trọn đời, vĩnh cửu kh&ocirc;ng thay đổi...&nbsp;</p>\r\n\r\n<p><strong>C&acirc;y sen đ&aacute;</strong>&nbsp;l&agrave; d&ograve;ng thực vật mọng nước (<strong>Succulent plant</strong>) thuộc chi&nbsp;<strong>Echeveria</strong>&nbsp;họ thuốc bỏng (Crassulaceae). Ước t&iacute;nh c&oacute; khoảng 60 họ sen đ&aacute; kh&aacute;c nhau với&nbsp;gần 400&nbsp;lo&agrave;i, trong đ&oacute; hơn 90% ph&acirc;n bố chủ yếu ở v&ugrave;ng n&oacute;ng gần x&iacute;ch đạo&nbsp;<strong>Mexico</strong>,&nbsp;<strong>Nam&nbsp;Mỹ</strong>,&nbsp;<strong>ch&acirc;u &Uacute;c</strong>&nbsp;v&agrave;&nbsp;<strong>ch&acirc;u Phi</strong>.&nbsp;</p>\r\n\r\n<p>Tại Việt Nam,&nbsp;<strong>sen đ&aacute;</strong>&nbsp;rất được ưa chuộng bởi đ&acirc;y l&agrave; loại c&acirc;y mới lạ c&oacute; kỹ thuật trồng đơn giản v&agrave; dễ chăm s&oacute;c.&nbsp;<strong>Sen đ&aacute;</strong>&nbsp;c&oacute; thể dễ d&agrave;ng bố tr&iacute; kết hợp với rất nhiều loại vật liệu chứa kh&aacute;c nhau để trở th&agrave;nh&nbsp;<strong>vật trang tr&iacute; trong nh&agrave;</strong>&nbsp;như: B&igrave;nh hoa tiểu cảnh thủy sinh, Tranh sen đ&aacute; treo tường, lọ sen đ&aacute; mini trang tr&iacute; b&agrave;n l&agrave;m việc...</p>\r\n\r\n<p>L&aacute;&nbsp;<strong>c&acirc;y sen đ&aacute;</strong>&nbsp;nhỏ mọng nước, xếp v&ograve;ng tr&ograve;n quanh t&acirc;m th&agrave;nh hoa như hoa sen, tr&ocirc;ng rất đẹp v&agrave; xinh xắn. Khi đ&oacute;n nhiều nắng, l&aacute; c&acirc;y chuyển m&agrave;u n&acirc;u đỏ thậm ch&iacute; m&agrave;u t&iacute;m biếc, c&oacute; lẽ ch&iacute;nh v&igrave; thế m&agrave; khi được nhập về Việt Nam, c&acirc;y được gọi ngay&nbsp;l&agrave;&nbsp;<strong>hoa&nbsp;sen đ&aacute;</strong>.</p>\r\n\r\n<p><strong>Sen đ&aacute;</strong>&nbsp;l&agrave; lo&agrave;i&nbsp;<strong>c&acirc;y ưa nắng</strong>, thường sống ở c&aacute;c&nbsp;<strong>v&ugrave;ng đất kh&ocirc; cằn</strong>, thiếu chất dinh dưỡng v&agrave; kh&ocirc; n&oacute;ng như sa mạc, v&ugrave;ng n&uacute;i đ&aacute;. L&aacute; c&acirc;y d&agrave;y, mọng ch&iacute;nh l&agrave; để t&iacute;ch nước duy tri sự sống qua những ng&agrave;y hạn k&eacute;o d&agrave;i.</p>\r\n', 35, '1', '2021-12-09 16:45:58', '2_CCSD'),
(94, 'Giới thiệu về xương rồng', '7a692fe147eda436ac377c755f1e8a20.jpg', '<p>Họ Xương rồng&nbsp;(danh ph&aacute;p khoa học:&nbsp;Cactaceae) thường l&agrave; c&aacute;c lo&agrave;i c&acirc;y mọng nước hai l&aacute; mầm v&agrave; c&oacute; hoa. Họ Cactaceae c&oacute; từ 25 đến 220 chi, t&ugrave;y theo nguồn (90 chi phổ biến nhất), trong đ&oacute; c&oacute; từ 1.500 đến 1.800 lo&agrave;i.</p>\r\n', '<p>Xương rồng l&agrave; một lo&agrave;i thực vật mọng nước, c&oacute; nhiều dạng ph&aacute;t triển: th&agrave;nh c&acirc;y lớn, th&agrave;nh bụi hoặc phủ s&aacute;t mặt đất. Đa số c&aacute;c lo&agrave;i xương rồng đều mọc v&agrave; ph&aacute;t triển từ đất, nhưng cũng c&oacute; rất nhiều lo&agrave;i k&yacute; sinh tr&ecirc;n c&aacute;c lo&agrave;i c&acirc;y kh&aacute;c để ph&aacute;t triển. Phần lớn xương rồng, trừ nh&aacute;nh&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Pereskioideae&amp;action=edit&amp;redlink=1\">Pereskioideae</a>&nbsp;ph&acirc;n loại dưới-họ (xem ảnh b&ecirc;n), c&oacute; l&aacute; ti&ecirc;u biến rất đ&aacute;ng kể. C&aacute;nh hoa ph&acirc;n bố đồng đều v&agrave; đồng t&acirc;m, hoa đa phần l&agrave; lưỡng t&iacute;nh, nở v&agrave;o cả s&aacute;ng v&agrave; tối tuỳ theo lo&agrave;i. H&igrave;nh dạng thay đổi từ dạng-phễu qua dạng-chu&ocirc;ng v&agrave; tới dạng-tr&ograve;n-phẳng, k&iacute;ch thước trong khoảng từ 0,2 đến 15&ndash;30&nbsp;cm. Phần lớn c&oacute; đ&agrave;i hoa (từ 5-50 c&aacute;i hoặc hơn), thay đổi dạng từ ngo&agrave;i v&agrave;o trong, từ&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=L%C3%A1_b%E1%BA%AFc&amp;action=edit&amp;redlink=1\">l&aacute; bắc</a><a href=\"https://vi.wikipedia.org/wiki/H%E1%BB%8D_X%C6%B0%C6%A1ng_r%E1%BB%93ng#cite_note-3\">[3]</a>&nbsp;đến&nbsp;<a href=\"https://vi.wikipedia.org/wiki/C%C3%A1nh_hoa\">c&aacute;nh hoa</a>. Số lượng nhị rất lớn, từ 50 đến 1.500 (hiếm khi &iacute;t hơn). Gần như tất cả c&aacute;c lo&agrave;i xương rồng c&oacute; vị đắng, thi thoảng b&ecirc;n trong c&ograve;n c&oacute; nhựa đục. Một tr&aacute;i xương rồng chứa khoảng 3.000 hạt, mỗi hạt d&agrave;i 0,4-12mm.<a href=\"https://vi.wikipedia.org/wiki/H%E1%BB%8D_X%C6%B0%C6%A1ng_r%E1%BB%93ng#cite_note-4\">[4]</a></p>\r\n\r\n<p>Hoa xương rồng ở&nbsp;<strong>Vườn thực vật&nbsp;<em>Bay Area</em></strong></p>\r\n\r\n<p>Trung b&igrave;nh, một c&acirc;y xương rồng sống rất l&acirc;u, tới hơn 300 năm, v&agrave; cũng c&oacute; lo&agrave;i chỉ sống 25 năm. Lo&agrave;i xương rồng&nbsp;<strong>Saguaro</strong>&nbsp;(<em><a href=\"https://vi.wikipedia.org/wiki/Carnegiea_gigantea\">Carnegiea gigantea</a></em>) c&oacute; thể cao tới 15m (kỉ lục đo được l&agrave; 17m67, trong khi đ&oacute; 10 năm đầu n&oacute; chỉ cao 10&nbsp;cm. C&acirc;y xương rồng &quot;Gối b&ocirc;ng của mẹ chồng&quot; (&quot;mother-in-law&#39;s cushion&quot;,&nbsp;<em><a href=\"https://vi.wikipedia.org/wiki/Echinocactus_grusonii\">Echinocactus grusonii</a></em>) nhỏ nhất ở&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Qu%E1%BA%A7n_%C4%91%E1%BA%A3o_Canaria\">quần đảo Canaria</a>&nbsp;cao 2m50 v&agrave; đường k&iacute;nh l&agrave; 1m, cho b&ocirc;ng mỗi 6 năm. Đường k&iacute;nh hoa xương rồng khoảng 5&ndash;30&nbsp;cm m&agrave;u sắc rất sặc sỡ, lộng lẫy.</p>\r\n', 35, '1', '2021-12-09 16:49:51', '3_SRC'),
(95, 'Giới thiệu về cây cảnh trong nhà', 'cay-xanh-trong-trang-tri-thiet-ke-nha-pho-hien-dai-cay-xanh-gop-phan-dieu-hoa-khong-khi-cho-khong-gian-song.jpg', '<p>C&acirc;y cảnh (hoặc c&acirc;y kiểng)&nbsp;l&agrave; một số loại thực vật được chăm s&oacute;c, gieo trồng v&agrave; tạo d&aacute;ng c&ocirc;ng phu, thường d&ugrave;ng l&agrave;m vật trang tr&iacute; hay một chi tiết trong thuật phong thủy. Kiến tr&uacute;c v&agrave; kh&ocirc;ng gian trong nh&agrave; sẽ bị th&ocirc; nếu kh&ocirc;ng c&oacute; sự g&oacute;p mặt của c&acirc;y xanh.</p>\r\n', '<p>C&acirc;y cảnh (hoặc c&acirc;y kiểng)&nbsp;l&agrave; một số loại thực vật được chăm s&oacute;c, gieo trồng v&agrave; tạo d&aacute;ng c&ocirc;ng phu, thường d&ugrave;ng l&agrave;m vật trang tr&iacute; hay một chi tiết trong thuật phong thủy. Kiến tr&uacute;c v&agrave; kh&ocirc;ng gian trong nh&agrave; sẽ bị th&ocirc; nếu kh&ocirc;ng c&oacute; sự g&oacute;p mặt của c&acirc;y xanh. Tuy nhi&ecirc;n, kh&ocirc;ng phải loại c&acirc;y n&agrave;o cũng ph&ugrave; hợp.C&oacute; rất nhiều c&aacute;c loại c&acirc;y nội thất m&agrave; bạn c&oacute; thể lựa chọn trang tr&iacute; trong nh&agrave; t&ugrave;y v&agrave;o điều kiện kinh ph&iacute;, kh&ocirc;ng gian, phong thủy, vị tr&iacute;&hellip; Đến với vựa c&acirc;y kiểng,&nbsp;Biển V&agrave;ng Luxury&nbsp;xin giới thiệu đến bạn top &nbsp;loại c&acirc;y cảnh nội thất trong nh&agrave;, vừa gi&uacute;p cho kh&ocirc;ng gian sống trở n&ecirc;n gần gũi với thi&ecirc;n nhi&ecirc;n hơn, m&agrave; c&ograve;n mang đến những gi&aacute; trị phong thủy&nbsp; t&agrave;i lộc cho gia đinh bạn.</p>\r\n', 35, '1', '2021-12-09 16:43:35', '1_CCTN');

