--
-- Cấu trúc bảng cho bảng `products`
--

CREATE TABLE `products` (
  `productCode` int(11) NOT NULL,
  `productImage` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `productName` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `productLineCode` varchar(10) COLLATE utf8mb4_unicode_ci NOT NULL,
  `Unit` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL,
  `Size` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `productVender` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `productDecription` varchar(5000) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `quantityEntered` smallint(6) NOT NULL,
  `quantitySold` smallint(6) DEFAULT NULL,
  `quantityInStock` smallint(6) DEFAULT NULL,
  `buyPrice` smallint(6) NOT NULL,
  `status` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

