INSERT INTO `products` (`productCode`, `productImage`, `productName`, `productLineCode`, `Unit`, `Size`, `productVender`, `productDecription`, `quantityEntered`, `quantitySold`, `quantityInStock`, `buyPrice`, `status`) VALUES
(1, 'sennhungvientrang.jpg', 'Sen nhung viền đen', '2_CCSD', 'Cây', '3-5cm', 'Đà Lạt', '<p>Sen Đ&aacute; Nhung Viền Đen (t&ecirc;n khoa học:&nbsp;<em>Kalanchoe tomentosa &lsquo;Chocolate Soldier&rsquo;</em>) c&oacute; nguồn gốc từ Mexico. C&acirc;y c&oacute; l&aacute; tương đối d&agrave;y v&agrave; được bao phủ bởi một lớp l&ocirc;ng mỏng, xung quanh l&aacute; c&oacute; viền m&agrave;u n&acirc;u đen. V&igrave; vậy n&ecirc;n ch&uacute;ng được gọi l&agrave; Sen Đ&aacute; Nhung Viền Đen.</p>\r\n\r\n<p>Loại sen đ&aacute; n&agrave;y c&oacute; hoa m&agrave;u v&agrave;ng, h&igrave;nh chu&ocirc;ng, thường nở v&agrave;o th&aacute;ng 4 đến th&aacute;ng 6 hằng năm. C&agrave;nh c&acirc;y nhỏ v&agrave; ph&acirc;n nh&aacute;nh ở gốc. C&acirc;y thường mọc th&agrave;nh những bụi nhỏ cao khoảng 35-45cm.</p>\r\n\r\n<h2>&Yacute; nghĩa của c&acirc;y</h2>\r\n\r\n<p>Sen Đ&aacute; Nhung Viền Đen tượng trưng cho l&ograve;ng son sắt trong t&igrave;nh bạn, t&igrave;nh y&ecirc;u, kh&ocirc;ng thay đổi theo thời gian. Từ sức sống của sen đ&aacute;, người ta thường quan niệm rằng mọi chuyện đều c&oacute; thể vượt qua miễn l&agrave; con người biết ki&ecirc;n nhẫn v&agrave; dũng cảm đối diện.&nbsp;V&igrave; vậy h&atilde;y lu&ocirc;n tin tưởng v&agrave;o cuộc sống, mạnh mẽ vươn l&ecirc;n.</p>\r\n\r\n<p>Trong phong thủy c&acirc;y c&ograve;n tượng trưng cho sự may mắn, t&agrave;i lộc. Đặt c&acirc;y tr&ecirc;n b&agrave;n l&agrave;m việc gi&uacute;p cho sự nghiệp thăng tiến, thuận lợi. B&ecirc;n cạnh đ&oacute;, c&acirc;y mang lại kh&ocirc;ng gian tươi m&aacute;t cho ng&ocirc;i nh&agrave; của bạn, xua tan phiền muộn lo toan trong đời sống.</p>\r\n\r\n<h2>C&aacute;ch chăm s&oacute;c c&acirc;y</h2>\r\n\r\n<p>Sen đ&aacute; c&oacute; những đặc t&iacute;nh gần giống với họ xương rồng. Ch&uacute;ng đều l&agrave; những c&acirc;y ưa hạn, sinh trưởng v&agrave; ph&aacute;t triển tốt trong điều kiện m&ocirc;i trường khắc nghiệt. Do đ&oacute; bạn sẽ kh&ocirc;ng mất qu&aacute; nhiều thời gian v&agrave; c&ocirc;ng sức để chăm s&oacute;c.</p>\r\n', 30, 0, 30, 10, '1'),
